import React, { useState, useEffect } from 'react';
import { View, Text, ScrollView, Image, StyleSheet, TouchableOpacity } from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import { useNavigation } from '@react-navigation/native';
import AsyncStorage from '@react-native-async-storage/async-storage';

interface JsonPlaceholder {
  id: string;
  name: string;
  image: string;
  description: string;
  price: number;
  userId: string;  // Asumiendo que cada juego tiene un userId asociado
}

const fetchData = async (userId: string | null) => {
  const response = await fetch(`http://localhost/geingeemu/public/api/videogame_index?userId=${userId}`);
  return await response.json();
}

const GamesView = ({ data }: { data: JsonPlaceholder[] }) => {
  const navigation = useNavigation();

  const renderGameItem = (game: JsonPlaceholder) => (
    <TouchableOpacity onPress={() => navigation.navigate('GameDetails', { game })}>
      <View style={styles.gameItem}>
        <Image source={{ uri: game.image }} style={styles.image} />
        <Text style={styles.name}>{game.name}</Text>
        <Text style={styles.price}>${game.price}</Text>
      </View>
    </TouchableOpacity>
  );

  return (
    <View style={styles.container}>
      <TouchableOpacity onPress={() => navigation.goBack()} style={styles.goBack}>
        <Ionicons name="arrow-back" size={25} />
        <Text>Back</Text>
      </TouchableOpacity>
      <Text style={styles.header}>Your Games</Text>
      <ScrollView contentContainerStyle={styles.gamesContainer}>
        {data.map(renderGameItem)}
      </ScrollView>
    </View>
  );
}

const MainScreen = () => {
  const [games, setGames] = useState<JsonPlaceholder[]>([]);
  const [userId, setUserId] = useState<string | null>(null);

  useEffect(() => {
    const fetchUserId = async () => {
      const storedUserId = await AsyncStorage.getItem('userId');
      setUserId(storedUserId);
    };
    fetchUserId();
  }, []);

  useEffect(() => {
    if (userId) {
      fetchData(userId).then(setGames);
    }
  }, [userId]);

  return (
    <View style={{ flex: 1 }}>
      <GamesView data={games} />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 16,
  },
  gameItem: {
    marginBottom: 16,
    alignItems: 'center',
  },
  image: {
    width: '100%',
    height: 200,
  },
  name: {
    fontSize: 20,
    fontWeight: 'bold',
    marginTop: 8,
  },
  price: {
    fontSize: 18,
    color: 'green',
    marginTop: 4,
  },
  header: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 20,
  },
  goBack: {
    flexDirection: 'row',
    alignItems: 'center',
    marginBottom: 10,
  },
  gamesContainer: {
    alignItems: 'center',
  }
});

export default MainScreen;
