import React, { useState, useEffect } from 'react';
import { View, Text, TextInput, Button, CheckBox } from 'react-native';
import axios from 'axios';

interface FormData {
  name: string;
  description: string;
  stock: number;
  price: number;
  physical: boolean;
  digital: boolean;
  image: string;
  id_category: string;
  id_genre: string;
  id_platform: string;
  id_brand: string;
}

function VideoGameForm() {
  const [formData, setFormData] = useState<FormData>({
    name: '',
    description: '',
    stock: 0,
    price: 0,
    physical: false,
    digital: false,
    image: '',
    id_category: '',
    id_genre: '',
    id_platform: '',
    id_brand: '',
  });

  const [categories, setCategories] = useState<any[]>([]);
  const [genres, setGenres] = useState<any[]>([]);
  const [platforms, setPlatforms] = useState<any[]>([]);
  const [brands, setBrands] = useState<any[]>([]);

  useEffect(() => {
    axios.get('http://localhost/geingeemu/public/api/categories')
      .then(response => setCategories(response.data))
      .catch(error => console.error('Error fetching categories:', error));

    axios.get('http://localhost/geingeemu/public/api/genres')
      .then(response => setGenres(response.data))
      .catch(error => console.error('Error fetching genres:', error));

    axios.get('http://localhost/geingeemu/public/api/platforms')
      .then(response => setPlatforms(response.data))
      .catch(error => console.error('Error fetching platforms:', error));

    axios.get('http://localhost/geingeemu/public/api/brands')
      .then(response => setBrands(response.data))
      .catch(error => console.error('Error fetching brands:', error));
  }, []);

  const handleChange = (name: string, value: any) => {
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = () => {
    const endpoint = 'http://localhost/geingeemu/public/api/videogamestore';

    axios.post(endpoint, formData)
      .then(response => {
        console.log('Videojuego created:', response.data);
      })
      .catch(error => {
        console.error('Create error:', error);
      });
  };

  return (
    <View>
      <Text>{isCreating ? 'ADD VIDEOGAME' : 'EDIT VIDEOGAME'}</Text>
      <TextInput
        placeholder="Name"
        value={formData.name}
        onChangeText={(text) => handleChange('name', text)}
      />
      <TextInput
        placeholder="Description"
        value={formData.description}
        onChangeText={(text) => handleChange('description', text)}
      />
      <TextInput
        placeholder="Stock"
        value={formData.stock.toString()}
        onChangeText={(text) => handleChange('stock', parseInt(text))}
      />
      <TextInput
        placeholder="Price"
        value={formData.price.toString()}
        onChangeText={(text) => handleChange('price', parseFloat(text))}
      />
      <CheckBox
        value={formData.physical}
        onValueChange={(value) => handleChange('physical', value)}
      />
      <CheckBox
        value={formData.digital}
        onValueChange={(value) => handleChange('digital', value)}
      />
      <TextInput
        placeholder="Image"
        value={formData.image}
        onChangeText={(text) => handleChange('image', text)}
      />
      {/* Render dropdown/select options for categories, genres, platforms, and brands */}
      <Button title={isCreating ? 'Create' : 'Save'} onPress={handleSubmit} />
    </View>
  );
}

export default VideoGameForm;
