import React, { useEffect, useState } from 'react';
import { FlatList, Text, View } from 'react-native';
import axios from 'axios';
import AsyncStorage from '@react-native-async-storage/async-storage';
import { useToken } from './AuthService';

interface Chat {
  id: number;
  id_user1: number;
  id_user2: number;
  id_videogame: number;
  chat: string;
  date: string;
}

const ChatList: React.FC = ({ navigation }) => {
  const token = useToken();
  const [chats, setChats] = useState<Chat[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchUserId = async () => {
      const storedUserId = await AsyncStorage.getItem('userId');
      if (storedUserId) {
        const userId = BigInt(storedUserId);
        try {
          const response = await axios.get<Chat[]>(`http://localhost/geingeemu/public/api/loadchats/1`, {
            headers: { 'Authorization': `Bearer ${token}` },
          });
          setChats(response.data);
        } catch (error) {
          console.error('Error al cargar los chats:', error);
        } finally {
          setLoading(false);
        }
      }
    };
    fetchUserId();
  }, [token]);

  const renderItem = ({ item }: { item: Chat }) => (
    <View style={{ padding: 10 }}>
      <Text>ID: {item.id}</Text>
      <Text>User 1: {item.id_user1}</Text>
      <Text>User 2: {item.id_user2}</Text>
      <Text>Videojuego: {item.id_videogame}</Text>
      <Text>Fecha: {item.date}</Text>
      <Text>Chat: {item.chat}</Text>
    </View>
  );

  if (loading) {
    return <Text>Cargando...</Text>;
  }

  if (chats.length === 0) {
    return <Text>No hay chats disponibles</Text>;
  }

  return (
    <FlatList
      data={chats}
      renderItem={renderItem}
      keyExtractor={(item) => item.id.toString()}
    />
  );
};

export default ChatList;
