import React, { useEffect, useState, useRef } from 'react';
import { StyleSheet, View, Text, Image, TouchableOpacity, ScrollView, ImageSourcePropType } from 'react-native';
import AsyncStorage from '@react-native-async-storage/async-storage';
import { StackNavigationProp } from '@react-navigation/stack';
import { RouteProp } from '@react-navigation/native';
import { RootStackParamList } from './App';
import { Ionicons } from '@expo/vector-icons';
import axios from 'axios';

type ProdInfoScreenNavigationProp = StackNavigationProp<RootStackParamList, 'ProdInfo'>;
type ProdInfoScreenRouteProp = RouteProp<RootStackParamList, 'ProdInfo'>;

type Props = {
  navigation: ProdInfoScreenNavigationProp;
  route: ProdInfoScreenRouteProp;
};

type Product = {
  id: number;
  name: string;
  price: number;
  description: string;
  image: ImageSourcePropType;
};

const ProdInfo: React.FC<Props> = ({ navigation, route }) => {
  const { item } = route.params;
  const [cartItems, setCartItems] = useState<Product[]>([]);
  const keyRef = useRef<string>(Date.now().toString()); // Referencia mutable que cambia en cada renderizado

  const addToCart = async (item: Product) => {
    try {
      const existingItems = await AsyncStorage.getItem('cartItems');
      let items: Product[] = [];
      if (existingItems !== null) {
        items = JSON.parse(existingItems);
      }
      items.push(item);
      await AsyncStorage.setItem('cartItems', JSON.stringify(items));
      setCartItems(items); // Actualizar el estado local con los nuevos elementos del carrito
      keyRef.current = Date.now().toString(); // Actualizar la referencia mutable para forzar el renderizado
      navigation.navigate('Cart');
    } catch (error) {
      console.error('Error adding to cart: ', error);
    }
  };
  const createChat = async () => {
    try {
      const newChat = {
        id_user1: userId,
        id_user2: item.id_user,
        id_videogame: item.id,
        chat: `[{"name":"${userData}","message":"Hola","date":"2024-05-06T12:00:00Z"}]`,
        date: '2024-07-05',
        // date: new Date().toISOString(),
      };

      // Enviar la solicitud para crear el nuevo chat
      await axios.post('http://localhost/geingeemu/public/api/newchat', newChat);

      // Actualizar el estado local o realizar cualquier otra acción necesaria

      // Refrescar la pantalla o navegar a otra pantalla si es necesario
      setKey(Date.now().toString());
      // navigation.navigate('ChatList'); // Por ejemplo, navegamos a la lista de chats después de crear uno nuevo
    } catch (error) {
      console.error('Error creating chat:', error);
    }
  };

  return (
    <ScrollView key={keyRef.current} contentContainerStyle={styles.container}>
      <TouchableOpacity onPress={() => navigation.goBack()} style={styles.goBackButton}>
        <Ionicons name="arrow-undo-sharp" size={25} />
        <Text>Go back</Text>
      </TouchableOpacity>

      <View style={styles.header}>
        <Text style={styles.headerText}>{item.name}</Text>
        <Text style={styles.price}>${item.price}</Text>
      </View>

      <View style={styles.imageContainer}>
        <Image source={item.image} style={styles.image} />
      </View>

      <View style={styles.descriptionContainer}>
        <Text style={styles.description}>{item.description}</Text>
      </View>

      <View style={styles.buttonContainer}>
        <TouchableOpacity style={[styles.button, styles.addToCartButton]} onPress={() => addToCart(item)}>
          <Text style={styles.buttonText}>Add to Cart</Text>
        </TouchableOpacity>

        <TouchableOpacity style={[styles.button, styles.startChatButton]} onPress={createChat}>
          <Ionicons name="chatbubble-ellipses-outline" size={25} />
          <Text style={styles.buttonText}>Start Chat</Text>
        </TouchableOpacity>
      </View>
    </ScrollView>
  );
};


const styles = StyleSheet.create({
  container: {
    flexGrow: 1,
    backgroundColor: '#f9f9f9',
    padding: 16,
  },
  header: {
    backgroundColor: '#ffffff',
    padding: 20,
    borderRadius: 10,
    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.25,
    shadowRadius: 3.84,
    elevation: 5,
    marginBottom: 20,
  },
  headerText: {
    fontSize: 22,
    fontWeight: 'bold',
    color: '#333',
  },
  price: {
    fontSize: 18,
    color: '#2a9d8f',
    marginTop: 8,
  },
  imageContainer: {
    marginBottom: 20,
    borderRadius: 10,
    overflow: 'hidden',
  },
  image: {
    width: '50%',
    height: 250,
    resizeMode: 'cover',
    alignSelf: 'center'
  },
  descriptionContainer: {
    backgroundColor: '#ffffff',
    padding: 20,
    borderRadius: 10,
    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.25,
    shadowRadius: 3.84,
    elevation: 5,
    marginBottom: 20,
  },
  description: {
    fontSize: 16,
    color: '#666',
  },
  buttonContainer: {
    alignItems: 'center',
  },
  button: {
    backgroundColor: '#e76f51',
    paddingVertical: 12,
    paddingHorizontal: 40,
    borderRadius: 20,
    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.25,
    shadowRadius: 3.84,
    elevation: 5,
    marginBottom: 10,
  },
  buttonText: {
    color: '#ffffff',
    fontSize: 18,
    fontWeight: 'bold',
  },
});

export default ProdInfo;
