import React, { createContext, useState, useContext, ReactNode } from 'react';
import { MD3LightTheme, MD3DarkTheme, PaperTheme } from 'react-native-paper';

type ThemeContextType = {
  theme: PaperTheme;
  toggleTheme: () => void;
};

const ThemeContext = createContext<ThemeContextType>({
  theme: MD3LightTheme, // Utiliza MD3LightTheme como predeterminado
  toggleTheme: () => {},
});

type ThemeProviderProps = {
  children: ReactNode;
};

export const ThemeProvider = ({ children }: ThemeProviderProps) => {
  const [theme, setTheme] = useState<PaperTheme>(MD3LightTheme); // Inicializado con MD3LightTheme

  const toggleTheme = () => {
    setTheme(theme => theme === MD3LightTheme ? MD3DarkTheme : MD3LightTheme);
  };

  return (
    <ThemeContext.Provider value={{ theme, toggleTheme }}>
      {children}
    </ThemeContext.Provider>
  );
};

export const useTheme = () => useContext(ThemeContext);
