import React, { useEffect, useState } from 'react';
import { View, Text, FlatList, StyleSheet, TouchableOpacity } from 'react-native';
import axios from 'axios';
import { RouteProp } from '@react-navigation/native';
import { Ionicons } from '@expo/vector-icons';

interface ChatMessage {
    nombre: string;
    mensaje: string;
    fecha: string;
}

interface Chat {
    id: number;
    id_user1: number;
    id_user2: number;
    id_videogame: number;
    chat: ChatMessage[];
    date: string;
}

const ChatDetailScreen: React.FC<{ route: any }> = ({ navigation, route }) => {
    const { chatId } = route.params;
    const [messages, setMessages] = useState<Chat[]>([]);

    useEffect(() => {
        const fetchMessages = async () => {
            try {
                const response = await axios.get<Chat[]>(`http://localhost/geingeemu/public/api/loadchats/${chatId}`);
                console.log('Mensajes del chat:', response.data.chat);
                setMessages(response.data.chat);
            } catch (error) {
                console.error('Error al cargar los mensajes:', error);
            }
        };

        fetchMessages();
    }, [chatId]);

    const renderItem = ({ item }: { item: ChatMessage }) => (
        <View style={styles.messageContainer}>
            <Text style={styles.messageTitle}>{item.nombre}</Text>
            <Text>{item.mensaje}</Text>
            <Text style={styles.messageDate}>{item.fecha}</Text>
        </View>
    );

    return (
        <View>
            <TouchableOpacity onPress={() => navigation.goBack()}
                style={{
                    marginBottom: 10
                }}
            >
                <Ionicons name="arrow-undo-sharp" size={25} />
                <Text>Go back</Text>
            </TouchableOpacity>
            <FlatList
                data={messages}
                renderItem={renderItem}
                keyExtractor={(item, index) => index.toString()}
            />
        </View>
    );
};

const styles = StyleSheet.create({
    messageContainer: {
        padding: 10,
        borderBottomWidth: 1,
        borderBottomColor: '#ccc',
    },
    messageTitle: {
        fontWeight: 'bold',
    },
    messageDate: {
        color: '#666',
        fontSize: 12,
    },
});

export default ChatDetailScreen;
