import React, { useEffect, useState } from 'react';
import { StyleSheet, View, Text, Image, TouchableOpacity, ScrollView, ImageSourcePropType } from 'react-native';
import AsyncStorage from '@react-native-async-storage/async-storage';
import { StackNavigationProp } from '@react-navigation/stack';
import { RouteProp } from '@react-navigation/native';
import { RootStackParamList } from './App';

type ProdInfoScreenNavigationProp = StackNavigationProp<RootStackParamList, 'ProdInfo'>;
type ProdInfoScreenRouteProp = RouteProp<RootStackParamList, 'ProdInfo'>;

type Props = {
  navigation: ProdInfoScreenNavigationProp;
  route: ProdInfoScreenRouteProp;
};

type Product = {
  name: string;
  price: number;
  description: string;
  image: ImageSourcePropType;
};

const ProdInfo: React.FC<Props> = ({ navigation, route }) => {
  const { item } = route.params;
  const [key, setKey] = useState(Date.now().toString());
  const [isInWishlist, setIsInWishlist] = useState(false);

  useEffect(() => {
    const checkWishlist = async () => {
      try {
        const wishlistItems = await AsyncStorage.getItem('wishlistItems');
        if (wishlistItems !== null) {
          const items: Product[] = JSON.parse(wishlistItems);
          const isInWishlist = items.some((wishlistItem) => wishlistItem.name === item.name);
          setIsInWishlist(isInWishlist);
        }
      } catch (error) {
        console.error('Error checking wishlist: ', error);
      }
    };
    checkWishlist();
  }, []);

  const toggleWishlist = async () => {
    try {
      let wishlistItems: Product[] = [];
      const existingWishlist = await AsyncStorage.getItem('wishlistItems');
      if (existingWishlist !== null) {
        wishlistItems = JSON.parse(existingWishlist);
      }
      if (isInWishlist) {
        wishlistItems = wishlistItems.filter((wishlistItem) => wishlistItem.name !== item.name);
      } else {
        wishlistItems.push(item);
      }
      await AsyncStorage.setItem('wishlistItems', JSON.stringify(wishlistItems));
      setIsInWishlist(!isInWishlist);
      // Navigate to Wishlist screen after toggling wishlist
      navigation.navigate('Wishlist');
    } catch (error) {
      console.error('Error toggling wishlist: ', error);
    }
  };

  const addToCart = async (item: Product) => {
    try {
      const existingItems = await AsyncStorage.getItem('cartItems');
      let items: Product[] = [];
      if (existingItems !== null) {
        items = JSON.parse(existingItems);
      }
      items.push(item);
      await AsyncStorage.setItem('cartItems', JSON.stringify(items));
      setKey(Date.now().toString());
      navigation.navigate('Cart');
    } catch (error) {
      console.error('Error adding to cart: ', error);
    }
  };

  return (
    <ScrollView key={key} contentContainerStyle={styles.container}>
      <View style={styles.header}>
        <Text style={styles.headerText}>{item.name}</Text>
        <Text style={styles.price}>${item.price}</Text>
      </View>

      <View style={styles.imageContainer}>
        <Image source={item.image} style={styles.image} />
      </View>

      <View style={styles.descriptionContainer}>
        <Text style={styles.description}>{item.description}</Text>
      </View>

      <View style={styles.buttonContainer}>
        <TouchableOpacity style={[styles.button, { backgroundColor: isInWishlist ? '#ffcccc' : '#e76f51' }]} onPress={toggleWishlist}>
          <Text style={styles.buttonText}>{isInWishlist ? 'Remove from Wishlist' : 'Add to Wishlist'}</Text>
        </TouchableOpacity>
        <TouchableOpacity style={styles.button} onPress={() => addToCart(item)}>
          <Text style={styles.buttonText}>Add to Cart</Text>
        </TouchableOpacity>
      </View>
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: {
    flexGrow: 1,
    backgroundColor: '#f9f9f9',
    padding: 20,
  },
  header: {
    backgroundColor: '#ffffff',
    padding: 20,
    borderRadius: 10,
    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.25,
    shadowRadius: 3.84,
    elevation: 5,
    marginBottom: 20,
  },
  headerText: {
    fontSize: 22,
    fontWeight: 'bold',
    color: '#333',
  },
  price: {
    fontSize: 18,
    color: '#2a9d8f',
    marginTop: 8,
  },
  imageContainer: {
    marginBottom: 20,
    borderRadius: 10,
    overflow: 'hidden',
  },
  image: {
    width: '50%',
    height: 250,
    resizeMode: 'cover',
    alignSelf: 'center'
  },
  descriptionContainer: {
    backgroundColor: '#ffffff',
    padding: 20,
    borderRadius: 10,
    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.25,
    shadowRadius: 3.84,
    elevation: 5,
    marginBottom: 20,
  },
  description: {
    fontSize: 16,
    color: '#666',
  },
  buttonContainer: {
    alignItems: 'center',
  },
  button: {
    backgroundColor: '#e76f51',
    paddingVertical: 12,
    paddingHorizontal: 40,
    borderRadius: 20,
    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.25,
    shadowRadius: 3.84,
    elevation: 5,
    marginBottom: 10,
  },
  buttonText: {
    color: '#ffffff',
    fontSize: 18,
    fontWeight: 'bold',
  },
});

export default ProdInfo;
