import React, { useEffect, useState } from 'react';
import { View, Text, FlatList, StyleSheet, TouchableOpacity, TextInput, KeyboardAvoidingView, Platform } from 'react-native';
import axios from 'axios';
import { RouteProp } from '@react-navigation/native';
import { Ionicons } from '@expo/vector-icons';
import AsyncStorage from '@react-native-async-storage/async-storage';

interface ChatMessage {
  nombre: string;
  mensaje: string;
  fecha: string;
}

const ChatDetailScreen: React.FC<{ route: any }> = ({ navigation, route }) => {
  const { chatId } = route.params;
  const [messages, setMessages] = useState<ChatMessage[]>([]);
  const [newMessage, setNewMessage] = useState('');
  const [users, setUsers] = useState<{ [id: number]: string }>({});
  const [userId, setUserId] = useState<string | null>(null);

  useEffect(() => {
    const fetchMessages = async () => {
      try {
        const response = await axios.get<{ chat: string }[]>(`http://localhost/geingeemu/public/api/loadmessages/${chatId}`);
        if (response.data.length > 0) {
          const chatData = response.data[0].chat;
          const parsedChatData: ChatMessage[] = JSON.parse(chatData);
          setMessages(parsedChatData);
          
          useEffect(() => {
            const fetchUserId = async () => {
              const storedUserId = await AsyncStorage.getItem('userId');
              setUserId(storedUserId);
            };
        
            fetchUserId();
          }, []);
          const userIds = Array.from(new Set(parsedChatData.map(msg => msg.id_user)));
          const usersData = await Promise.all(userIds.map(userId => axios.get<{ firstname: string; lastname: string }>(`http://localhost/geingeemu/public/api/userview/${userId}`)));
          const usersMap: { [id: number]: string } = {};
          usersData.forEach(userData => {
            usersMap[userData.data.id] = `${userData.data.firstname} ${userData.data.lastname}`;
          });
          setUsers(usersMap);
        }
      } catch (error) {
        console.error('Error al cargar los mensajes:', error);
      }
    };

    fetchMessages();
  }, [chatId]);

  const handleSend = async () => {
    try {
      const newMessageData: ChatMessage = {
        nombre: 'Usuario', // Establecer el nombre del usuario que envÃ­a el mensaje
        mensaje: newMessage,
        fecha: new Date().toISOString(),
      };

      const updatedMessages = [...messages, newMessageData];
      setMessages(updatedMessages);

      await axios.post(`http://localhost/geingeemu/public/api/updatemessages/${chatId}`, { chat: JSON.stringify(updatedMessages) });

      setNewMessage('');
    } catch (error) {
      console.error('Error al enviar el mensaje:', error);
    }
  };

  const renderItem = ({ item }: { item: ChatMessage }) => (
    <View style={styles.messageContainer}>
      <Text style={styles.messageTitle}>{users[item.id_user]}</Text>
      <Text>{item.mensaje}</Text>
      <Text style={styles.messageDate}>{item.fecha}</Text>
    </View>
  );

  const handleGoBack = () => {
    navigation.goBack();
  };

  return (
    <KeyboardAvoidingView
      style={styles.container}
      behavior={Platform.OS === 'ios' ? 'padding' : 'height'}
    >
      <TouchableOpacity onPress={handleGoBack} style={styles.goBackButton}>
        <Ionicons name="arrow-undo-sharp" size={25} />
        <Text style={styles.goBackText}>Go back</Text>
      </TouchableOpacity>
      <FlatList
        data={messages}
        renderItem={renderItem}
        keyExtractor={(item, index) => index.toString()}
      />
      <TextInput
        style={styles.input}
        value={newMessage}
        onChangeText={setNewMessage}
        placeholder="Escribe un mensaje..."
        onSubmitEditing={handleSend}
      />
    </KeyboardAvoidingView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 10,
  },
  goBackButton: {
    flexDirection: 'row',
    alignItems: 'center',
    marginBottom: 10,
  },
  goBackText: {
    marginLeft: 5,
  },
  messageContainer: {
    padding: 10,
    borderBottomWidth: 1,
    borderBottomColor: '#ccc',
  },
  messageTitle: {
    fontWeight: 'bold',
  },
  messageDate: {
    color: '#666',
    fontSize: 12,
  },
  input: {
    borderWidth: 1,
    borderColor: '#ccc',
    borderRadius: 8,
    padding: 10,
    marginTop: 10,
  },
});

export default ChatDetailScreen;
