import React, { useState, useEffect } from 'react';
import { useToken } from './AuthService';
import AsyncStorage from '@react-native-async-storage/async-storage';
import axios from 'axios';
import { Button, View, Text, StyleSheet } from 'react-native';
interface UserData {
    firstname: string;
    lastname: string;
    birthdate: string;
    address: string;
    phone_number: string;
    email: string;
    role: string;
  }
  interface UserBiling{
    id: string;
    total: string;
    id_user: string;
    id_videogame: string;
    date: string;
  }
  interface VideoBiling{
    id: string;
    name: string;
    description: string;
    stock: string;
    price: string;
    physical: string;
    digital: string;
  }
  const Biling: React.FC = ({ navigation }) => {
    const token = useToken();  // Token fetched using custom hook
    const [userId, setUserId] = useState<string | null>(null);
    const [userData, setUserData] = useState<UserData | null>(null);
    const [bilingData, setBilingData] = useState<UserBiling | null>(null);
    const [videoData, setVideoData] = useState<VideoBiling | null>(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState<string | null>(null);
  
    useEffect(() => {
      const fetchUserId = async () => {
        const storedUserId = await AsyncStorage.getItem('userId');  // Retrieve the user ID from AsyncStorage
        setUserId(storedUserId);  // Update state with the retrieved ID
      };
  
      fetchUserId();  // Call the async function to fetch the user ID
    }, []);
  
    useEffect(() => {
      if (!userId || !token) return; // Only proceed if both userId and token are available
  
      const getUserDetails = async () => {
        try {
          const UserD = await axios.get(`http://localhost/geingeemu/public/api/userview/${userId}`, {
            headers: {
              'Authorization': `Bearer ${token}`,
            },
          });
          const BilingD = await axios.get(`http://localhost/geingeemu/public/api/bilingview/${userId}`, {
            headers: {
              'Authorization': `Bearer ${token}`,
            },
          });
          const VideoGD = await axios.get(`http://localhost/geingeemu/public/api/videogameview/${bilingData?.id_videogame}`, {
            headers: {
              'Authorization': `Bearer ${token}`,
            },
          });
          setUserData(UserD.data);
          setBilingData(BilingD.data);
          setVideoData(VideoGD.data);
          setLoading(false);
        } catch (error) {
          console.error('Fetch error:', error);
          setError('Error fetching user details.');
          setLoading(false);
        }
      };
  
      getUserDetails();
    }, [userId, token]);  // React when userId or token changes
  
    
  
    return (
      <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center' }}>
        {loading ? (
          <View style={styles.placeholder}>
            <Text>Loading...</Text>
          </View>
        ) : error ? (
          <Text style={{ color: 'red', marginBottom: 20 }}>{error}</Text>
        ) : userData ? (
          <View style={styles.container}>
            <Text style={{ fontSize: 24 }}>User Profile</Text>
            <Text>Id biling: {bilingData?.id}</Text>
            <Text>user: {userData.firstname},{userData.lastname}</Text>
            <Text>date of purchase: {bilingData?.date}</Text>

          </View>
        ) : (
          <Text>No user data available.</Text>
        )}
      </View>
    );
  };
  export default Biling;
