import React, { useState, useEffect } from 'react';
import { useToken } from './AuthService';
import AsyncStorage from '@react-native-async-storage/async-storage';
import axios from 'axios';
import { Button, View, Text, StyleSheet } from 'react-native';

interface UserData {
  firstname: string;
  lastname: string;
  birthdate: string;
  address: string;
  phone_number: string;
  email: string;
  role: string;
}

interface UserBiling {
  id: string;
  total: string;
  id_user: string;
  id_videogame: string;
  date: string;
}

interface VideoBiling {
  id: string;
  name: string;
  description: string;
  stock: string;
  price: string;
  physical: string;
  digital: string;
}

const Biling: React.FC = ({ navigation }) => {
  const token = useToken();  // Token fetched using custom hook
  const [userId, setUserId] = useState<string | null>(null);
  const [userData, setUserData] = useState<UserData | null>(null);
  const [bilingData, setBilingData] = useState<UserBiling | null>(null);
  const [videoData, setVideoData] = useState<VideoBiling | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const fetchUserId = async () => {
      const storedUserId = await AsyncStorage.getItem('userId');
      setUserId(storedUserId);
    };

    fetchUserId();
  }, []);

  useEffect(() => {
    const fetchData = async () => {
      if (!userId || !token) return;

      try {
        const userD = axios.get(`http://localhost/geingeemu/public/api/userview/${userId}`, {
          headers: { 'Authorization': `Bearer ${token}` },
        });
        const bilingD = axios.get(`http://localhost/geingeemu/public/api/bilingview/${userId}`, {
          headers: { 'Authorization': `Bearer ${token}` },
        });

        const [userResponse, bilingResponse] = await Promise.all([userD, bilingD]);
        setUserData(userResponse.data);
        setBilingData(bilingResponse.data);
        // If you need to fetch video game data, ensure bilingData is correctly set
        if (bilingResponse.data.id_videogame) {
          const videoGD = await axios.get(`http://localhost/geingeemu/public/api/videogameview/${bilingResponse.data.id_videogame}`, {
            headers: { 'Authorization': `Bearer ${token}` },
          });
          setVideoData(videoGD.data);
        }
        setLoading(false);
      } catch (error) {
        console.error('Fetch error:', error);
        setError('Failed to fetch data.');
        setLoading(false);
      }
    };

    fetchData();
  }, [userId, token]);  // Dependencies are correct for user ID and token

  return (
    <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center' }}>
      {loading ? (
        <View style={styles.placeholder}>
          <Text>Loading...</Text>
        </View>
      ) : error ? (
        <Text style={{ color: 'red', marginBottom: 20 }}>{error}</Text>
      ) : userData && bilingData ? (
        <View style={styles.container}>
          <Text style={{ fontSize: 24 }}>Biling {bilingData.id}</Text>
          <Text>Id biling: {bilingData.id}</Text>
          <Text>User: {userData.firstname}, {userData.lastname}</Text>
          <Text>Date of Purchase: {bilingData.date}</Text>
          {/* Optionally display video game details here */}
        </View>
      ) : (
        <Text>No user data available.</Text>
      )}
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    maxWidth: 800,
    width: '100%',
    padding: 20,
    backgroundColor: '#FFF',
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 0 },
    shadowOpacity: 0.1,
    shadowRadius: 10,
    alignItems: 'center',
  },
  placeholder: {
    justifyContent: 'center',
    alignItems: 'center',
    height: 400,
    backgroundColor: '#333',
    color: '#fff',
    fontSize: 20,
    borderRadius: 8,
  }
});

export default Biling;
