import React, { useState, useEffect } from 'react';
import { useToken } from './AuthService';
import AsyncStorage from '@react-native-async-storage/async-storage';
import axios from 'axios';
import { ScrollView, View, Text, StyleSheet, ActivityIndicator } from 'react-native';

interface VideoGame {
  id: string;
  name: string;
  description: string;
  stock: number;
  price: string;
  physical: boolean;
  digital: boolean;
}

interface GamesUserProps {
  navigation: any; // Specify more detailed type depending on your navigation library
}

const GamesUser: React.FC<GamesUserProps> = ({ navigation }) => {
  const token = useToken();
  const [userId, setUserId] = useState<string | null>(null);
  const [videoGames, setVideoGames] = useState<VideoGame[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const fetchUserId = async () => {
      const storedUserId = await AsyncStorage.getItem('userId');
      setUserId(storedUserId);
    };
    fetchUserId();
  }, []);

  useEffect(() => {
    const fetchData = async () => {
      if (!userId || !token) return;

      try {
        const response = await axios.get<{data: VideoGame[]}>(`http://localhost/geingeemu/public/api/ampp/${userId}`, {
          headers: { 'Authorization': `Bearer ${token}` },
        });
        if (Array.isArray(response.data.data)) {
          setVideoGames(response.data.data);
        } else {
          console.error('Unexpected data format:', response.data.data);
          setVideoGames([]); // Ensuring it's always an array
        }
        setLoading(false);
      } catch (error: any) {
        console.error('Fetch error:', error);
        setError('Failed to fetch data.');
        setLoading(false);
      }
    };

    fetchData();
  }, [userId, token]);

  const renderGames = () => {
    let content = [];
    for (let game of videoGames) {
      content.push(
        <View style={styles.card} key={game.id}>
          <Text style={styles.title}>{game.name}</Text>
          <Text style={styles.text}>Description: {game.description}</Text>
          <Text style={styles.text}>Price: {game.price}</Text>
          <Text style={styles.text}>Stock: {game.stock}</Text>
          <Text style={styles.text}>{game.physical ? 'Physical Copy Available' : 'Digital Only'}</Text>
        </View>
      );
    }
    return content;
  };

  return (
    <ScrollView contentContainerStyle={styles.container}>
      {loading ? (
        <ActivityIndicator size="large" color="#0000ff" />
      ) : error ? (
        <Text style={styles.error}>{error}</Text>
      ) : (
        renderGames()
      )}
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    padding: 10,
  },
  card: {
    backgroundColor: 'white',
    width: '90%',
    padding: 20,
    borderRadius: 10,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.3,
    shadowRadius: 4,
    elevation: 5,
    marginBottom: 20,
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 10,
  },
  text: {
    fontSize: 16,
    marginBottom: 5,
  },
  error: {
    color: 'red',
    fontSize: 18,
    marginBottom: 20,
  }
});

export default GamesUser;
