import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

type FormData = {
  name: string;
  description: string;
  stock: number;
  price: number;
  physical: boolean;
  digital: boolean;
  image: string;
  id_category: string;
  id_genre: string;
  id_platform: string;
  id_brand: string;
};

type Category = { id: string; name: string };
type Genre = { id: string; name: string };
type Platform = { id: string; name: string };
type Brand = { id: string; name: string };

function VideoGameForm() {
  const [show, setShow] = useState<boolean>(false);
  const target = useRef<HTMLButtonElement>(null);
  const { videoGameId } = useParams<{ videoGameId?: string }>();
  const isCreating = !videoGameId;

  const [formData, setFormData] = useState<FormData>({
    name: '',
    description: '',
    stock: 0,
    price: 0,
    physical: false,
    digital: false,
    image: '',
    id_category: '',
    id_genre: '',
    id_platform: '',
    id_brand: '',
  });

  const [categories, setCategories] = useState<Category[]>([]);
  const [genres, setGenres] = useState<Genre[]>([]);
  const [platforms, setPlatforms] = useState<Platform[]>([]);
  const [brands, setBrands] = useState<Brand[]>([]);

  useEffect(() => {
    axios.get('http://localhost/geingeemu/public/api/categories')
      .then(response => setCategories(response.data))
      .catch(error => console.error('Error fetching categories:', error));

    axios.get('http://localhost/geingeemu/public/api/genres')
      .then(response => setGenres(response.data))
      .catch(error => console.error('Error fetching genres:', error));

    axios.get('http://localhost/geingeemu/public/api/platforms')
      .then(response => setPlatforms(response.data))
      .catch(error => console.error('Error fetching platforms:', error));

    axios.get('http://localhost/geingeemu/public/api/brands')
      .then(response => setBrands(response.data))
      .catch(error => console.error('Error fetching brands:', error));

    if (!isCreating) {
      axios.get(`http://localhost/geingeemu/public/api/videogameview/${videoGameId}`)
        .then(response => setFormData(response.data))
        .catch(error => console.error('Error fetching videogame:', error));
    }
  }, [videoGameId, isCreating]);

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement>) => {
    const { name, value, type, checked } = e.target;
    setFormData({
      ...formData,
      [name]: type === 'checkbox' ? checked : value,
    });
  };

  const handleSubmit = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    const endpoint = isCreating
      ? 'http://localhost/geingeemu/public/api/videogamestore'
      : `http://localhost/geingeemu/public/api/videogameedit/${videoGameId}`;

    const method = isCreating ? 'post' : 'put';

    axios[method](endpoint, formData)
      .then(response => {
        console.log(`Videojuego ${isCreating ? 'created' : 'updated'}:`, response.data);
        setShow(true);
      })
      .catch(error => {
        console.error(`${isCreating ? 'Create' : 'Edit'} error:`, error);
        setShow(true);
      });
  };

  return (
    <View style={styles.container}>
     
    <label>Image URL:</label>
    <input type="text" name="image" value={formData.image} onChange={handleChange} />

    <label>Category:</label>
    <select name="id_category" value={formData.id_category} onChange={handleChange}>
      <option value="">Select a category</option>
      {categories.map(category => (
        <option key={category.id} value={category.id}>{category.name}</option>
      ))}
    </select>

    <label>Genre:</label>
    <select name="id_genre" value={formData.id_genre} onChange={handleChange}>
      <option value="">Select a genre</option>
      {genres.map(genre => (
        <option key={genre.id} value={genre.id}>{genre.name}</option>
      ))}
    </select>

    <label>Platform:</label>
    <select name="id_platform" value={formData.id_platform} onChange={handleChange}>
      <option value="">Select a platform</option>
      {platforms.map(platform => (
        <option key={platform.id} value={platform.id}>{platform.name}</option>
      ))}
    </select>

    <label>Brand:</label>
    <select name="id_brand" value={formData.id_brand} onChange={handleChange}>
      <option value="">Select a brand</option>
      {brands.map(brand => (
        <option key={brand.id} value={brand.id}>{brand.name}</option>
      ))}
    </select>
    </View>
  );
}

export default VideoGameForm;
