import React, { useEffect, useState } from 'react';
import { FlatList, Text, View } from 'react-native';
import axios from 'axios';

interface ChatMessage {
  nombre: string;
  mensaje: string;
  fecha: string;
}

interface ChatDetailScreenProps {
  route: { params: { chatId: number } };
}

const ChatDetailScreen: React.FC<ChatDetailScreenProps> = ({ navigation }) => {
  const { chatId } = route.params;
  const [chatMessages, setChatMessages] = useState<ChatMessage[]>([]);

  useEffect(() => {
    const loadChatMessages = async () => {
      try {
        const response = await axios.get<ChatMessage[]>(`http://localhost/geeingemu/public/api/loadchats/${chatId}`);
        setChatMessages(response.data);
      } catch (error) {
        console.error('Error al cargar los mensajes del chat:', error);
      }
    };

    loadChatMessages();
  }, [chatId]);

  const renderItem = ({ item }: { item: ChatMessage }) => (
    <View style={{ padding: 10 }}>
      <Text>Nombre: {item.nombre}</Text>
      <Text>Mensaje: {item.mensaje}</Text>
      <Text>Fecha: {item.fecha}</Text>
    </View>
  );

  return (
    <FlatList
      data={chatMessages}
      renderItem={renderItem}
      keyExtractor={(item, index) => index.toString()}
      ListEmptyComponent={<Text>No hay mensajes en este chat</Text>}
    />
  );
};

export default ChatDetailScreen;
