import React, { useEffect, useState } from 'react';
import { FlatList, Text, View } from 'react-native';
import axios from 'axios';

interface ChatMessage {
  nombre: string;
  mensaje: string;
  fecha: string;
}

interface Chat {
  id: number;
  id_user1: number;
  id_user2: number;
  id_videogame: number;
  chat: ChatMessage[];
  date: string;
}

const ChatsScreen: React.FC<{ userId: number }> = ({ userId }) => {
  const [chats, setChats] = useState<Chat[]>([]);

  useEffect(() => {
    const loadChats = async () => {
      try {
        const response = await axios.get<Chat[]>(`http://localhost/geingeemu/public/loadchats/${userId}`);
        setChats(response.data);
      } catch (error) {
        console.error('Error al cargar los chats:', error);
      }
    };

    loadChats();
  }, [userId]);

  const renderItem = ({ item }: { item: Chat }) => (
    <View style={{ padding: 10 }}>
      <Text>ID: {item.id}</Text>
      <Text>User 1: {item.id_user1}</Text>
      <Text>User 2: {item.id_user2}</Text>
      <Text>Videojuego: {item.id_videogame}</Text>
      <Text>Fecha: {item.date}</Text>
      <Text>Chat:</Text>
      <FlatList
        data={item.chat}
        renderItem={({ item: chatItem }) => (
          <View style={{ marginLeft: 10 }}>
            <Text>Nombre: {chatItem.nombre}</Text>
            <Text>Mensaje: {chatItem.mensaje}</Text>
            <Text>Fecha: {chatItem.fecha}</Text>
          </View>
        )}
        keyExtractor={(chatItem) => chatItem.fecha}
      />
    </View>
  );

  return (
    <FlatList
      data={chats}
      renderItem={renderItem}
      keyExtractor={(item) => item.id.toString()}
      ListEmptyComponent={<Text>No hay chats disponibles</Text>}
    />
  );
};

export default ChatsScreen;
