import React, { useState } from 'react';
import { NavigationContainer } from '@react-navigation/native';
import { createDrawerNavigator } from '@react-navigation/drawer';
import LoginScreen from './components/Login'; // Adjust the path as necessary
import Register from './components/Register'; // Adjust the path as necessary
import Home from './components/Home';
import Search from './components/Search';
import ProdInfo from './components/ProdInfo';
import Profile from './components/Profile';
import BilingView from './components/BilingView';
import GamesUser from './components/GamesUser';
import Addgame from './components/Addgame';
import Cart from './components/Cart';
import { AuthProvider } from './components/AuthService';

const Drawer = createDrawerNavigator();

const App = () => {
  const [cartItems, setCartItems] = useState<Product[]>([]); // State to manage cart items

  // Placeholder function to add item to cart
  const addToCart = (item: Product) => {
    // Check if the item is already in the cart
    const itemExists = cartItems.some((cartItem) => cartItem.name === item.name);

    if (itemExists) {
      // If the item is already in the cart, increase its quantity
      const updatedCartItems = cartItems.map((cartItem) =>
        cartItem.name === item.name ? { ...cartItem, quantity: cartItem.quantity + 1 } : cartItem
      );
      setCartItems(updatedCartItems);
    } else {
      // If the item is not in the cart, add it with quantity 1
      setCartItems([...cartItems, { ...item, quantity: 1 }]);
    }
  };

  // Placeholder function to remove item from cart
  const removeFromCart = (item: Product) => {
    // Filter out the item to be removed from the cart
    const updatedCartItems = cartItems.filter((cartItem) => cartItem.name !== item.name);
    setCartItems(updatedCartItems);
  };

  return (
    <NavigationContainer>
      <AuthProvider>
        <Drawer.Navigator initialRouteName="Home">
          <Drawer.Screen name="Home" component={Home} />
          <Drawer.Screen name="Profile" component={Profile} />
          <Drawer.Screen name="Login" component={LoginScreen} />
          <Drawer.Screen name="Register" component={Register} />
          <Drawer.Screen name="Search" component={Search} />
          {/* Pass addToCart function as a prop to ProdInfo */}
          <Drawer.Screen name="ProdInfo">
            {(props) => <ProdInfo {...props} addToCart={addToCart} />}
          </Drawer.Screen>
          <Drawer.Screen name="Biling" component={BilingView} />
          <Drawer.Screen name="Games" component={GamesUser} />
          <Drawer.Screen name="Addgame" component={Addgame} />
          {/* Pass cartItems and removeFromCart function as props to Cart */}
          <Drawer.Screen name="Cart">
            {(props) => <Cart {...props} cartItems={cartItems} removeFromCart={removeFromCart} />}
          </Drawer.Screen>
        </Drawer.Navigator>
      </AuthProvider>
    </NavigationContainer>
  );
};

export default App;
