import React, { useState, useEffect } from 'react';
import { useToken } from './AuthService';
import AsyncStorage from '@react-native-async-storage/async-storage';
import axios from 'axios';
import { ScrollView, View, Text, StyleSheet, ActivityIndicator } from 'react-native';

interface UserData {
  firstname: string;
  lastname: string;
  birthdate: string;
  address: string;
  phone_number: string;
  email: string;
  role: string;
}

interface UserBilling {
  id: string;
  total: string;
  id_user: string;
  id_videogame: string;
  date: string;
}

interface VideoGame {
  id: string;
  name: string;
  description: string;
  stock: string;
  price: string;
  physical: string;
  digital: string;
}

const Biling: React.FC = ({ navigation }) => {
  const token = useToken();
  const [userId, setUserId] = useState<string | null>(null);
  const [userData, setUserData] = useState<UserData | null>(null);
  const [billings, setBillings] = useState<UserBilling[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const fetchUserId = async () => {
      const storedUserId = await AsyncStorage.getItem('userId');
      setUserId(storedUserId);
    };
    fetchUserId();
  }, []);

  useEffect(() => {
    const fetchData = async () => {
      if (!userId || !token) return;

      try {
        const userResponse = await axios.get<UserData>(`http://localhost/geingeemu/public/api/userview/${userId}`, {
          headers: { 'Authorization': `Bearer ${token}` },
        });
        const billingResponse = await axios.get<UserBilling[]>(`http://localhost/geingeemu/public/api/bilingview/${userId}`, {
          headers: { 'Authorization': `Bearer ${token}` },
        });

        setUserData(userResponse.data);
        setBillings(billingResponse.data);

        setLoading(false);
      } catch (error) {
        console.error('Fetch error:', error);
        setError('Failed to fetch data.');
        setLoading(false);
      }
    };

    fetchData();
  }, [userId, token]);

  return (
    <ScrollView contentContainerStyle={styles.container}>
      {loading ? (
        <ActivityIndicator size="large" color="#0000ff" />
      ) : error ? (
        <Text style={styles.error}>{error}</Text>
      ) : (
        <View style={styles.card}>
          <Text style={styles.title}>Billing Details</Text>
          {userData && (
            <Text style={styles.text}>
              User: {userData.firstname} {userData.lastname}
            </Text>
          )}
          {billings.length > 0 && (
            billings.map((billing, index) => (
              <View key={index}>
                <Text style={styles.text}>Billing ID: {billing.id}</Text>
                <Text style={styles.text}>Date of Purchase: {billing.date}</Text>
                <Text style={styles.text}>Total: {billing.total}</Text>
              </View>
            ))
          )}
        </View>
      )}
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    padding: 10,
  },
  card: {
    backgroundColor: 'white',
    width: '90%',
    padding: 20,
    borderRadius: 10,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.3,
    shadowRadius: 4,
    elevation: 5,
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 10,
  },
  text: {
    fontSize: 16,
    marginBottom: 5,
  },
  error: {
    color: 'red',
    fontSize: 18,
    marginBottom: 20,
  }
});

export default Biling;
