import React, { useState, useEffect } from 'react';
import { useToken } from './AuthService';
import AsyncStorage from '@react-native-async-storage/async-storage';
import axios from 'axios';
import { ScrollView, View, Text, StyleSheet, Image, ActivityIndicator } from 'react-native';

interface VideoGame {
    id: number;
    name: string;
    description: string;
    stock: number;
    price: number;
    digital: boolean;
    physical: boolean;
    id_brand: number;
    id_category: number;
    id_genre: number;
    id_platform: number;
    id_user: number;
    image: string;
    type: number;
    created_at: string;
    updated_at: string;
  }
  

const GamesUser: React.FC = ({ navigation }) => {
  const token = useToken();
  const [userId, setUserId] = useState<string | null>(null);
  const [videoGames, setVideoGames] = useState<VideoGame[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const fetchUserId = async () => {
      const storedUserId = await AsyncStorage.getItem('userId');
      setUserId(storedUserId);
    };
    fetchUserId();
  }, []);

  useEffect(() => {
    const fetchData = async () => {
      if (!userId || !token) return;

      try {
        const response = await axios.get(`http://localhost/geingeemu/public/api/ampp/${userId}`, {
          headers: { 'Authorization': `Bearer ${token}` },
        });
        console.log("API Response:", response.data);  // Log the full response data
        const gamesData = response.data.map((game: any) => ({
          ...game,
          digital: game.digital === 1,
          physical: game.physical === 1,
        }));
        setVideoGames(gamesData);
        setLoading(false);
    } catch (error: any) {
        console.error('Fetch error:', error);
        setError('Failed to fetch data.');
        setLoading(false);
      }
    };

    fetchData();
  }, [userId, token]);

  return (
    <ScrollView contentContainerStyle={styles.container}>
      {loading ? (
        <ActivityIndicator size="large" color="#0000ff" />
      ) : error ? (
        <Text style={styles.error}>{error}</Text>
      ) : (
        videoGames.map(game => (
          <View style={styles.card} key={game.id}>
            <Text style={styles.title}>{game.name}</Text>
            <Image style={styles.image} source={{ uri: game.image }} />
            <Text style={styles.text}>Description: {game.description}</Text>
            <Text style={styles.text}>Price: {game.price}</Text>
            <Text style={styles.text}>Stock: {game.stock}</Text>
            <Text style={styles.text}>{game.physical ? 'Physical Copy Available' : 'Digital Only'}</Text>
          </View>
        ))
      )}
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    padding: 10,
  },
  card: {
    backgroundColor: 'white',
    width: '90%',
    padding: 20,
    borderRadius: 10,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.3,
    shadowRadius: 4,
    elevation: 5,
    marginBottom: 20,
  },
  image: {
    width: '100%',
    height: 200,
    marginBottom: 10,
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 10,
  },
  text: {
    fontSize: 16,
    marginBottom: 5,
  },
  error: {
    color: 'red',
    fontSize: 18,
    marginBottom: 20,
  }
});

export default GamesUser;
