import React, { useState } from 'react';
import { View, Text, TextInput, TouchableOpacity, Image, StyleSheet } from 'react-native';
import Login from './Login';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import { createDrawerNavigator } from '@react-navigation/drawer';
import { NavigationContainer } from '@react-navigation/native';

const handleBirthdateChange = (text) => {
  const newText = text.replace(/[^0-9]/g, ''); // Elimina caracteres que no sean n√∫meros
  let formattedText = '';

  // Aplicamos el formato DD-MM-YYYY con guiones
  if (newText.length <= 2) {
    formattedText = newText;
  } else if (newText.length <= 4) {
    formattedText = `${newText.slice(0, 2)}-${newText.slice(2)}`;
  } else if (newText.length <= 8) {
    formattedText = `${newText.slice(0, 2)}-${newText.slice(2, 4)}-${newText.slice(4)}`;
  }

  // Actualiza el estado con el texto formateado
  setBirthdate(formattedText);
};

const Registration = ({ navigation }) => {
  const [firstname, setFirstname] = useState('');
  const [lastname, setLastname] = useState('');
  const [birthdate, setBirthdate] = useState('');
  const [address, setAddress] = useState('');
  const [phone_number, setPhoneNumber] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [c_password, setCPassword] = useState('');
  const [role, setRole] = useState('');

  return (
    <View style={styles.container}>
      <Image source={require('../assets/logo.png')} style={styles.logo} />
      <Text style={styles.title}>Register</Text>
      <TextInput
        style={styles.input}
        placeholder="First Name"
        onChangeText={setFirstname}
        value={firstname}
      />
      <TextInput
        style={styles.input}
        placeholder="Last Name"
        onChangeText={setLastname}
        value={lastname}
      />
      <TextInput
        style={styles.input}
        placeholder="Birthdate (DD-MM-YYYY)"
        onChangeText={handleBirthdateChange}
        value={birthdate}
        keyboardType="numeric"  // Asegura que s√≥lo se muestre el teclado num√©rico
      />

      <TextInput
        style={styles.input}
        placeholder="Email"
        onChangeText={setEmail}
        value={email}
      />
      <TextInput
        style={styles.input}
        placeholder="Phone Number"
        keyboardType="phone-pad"
        onChangeText={setPhoneNumber}
        value={phone_number}
      />
      <TextInput
        style={styles.input}
        placeholder="Address"
        onChangeText={setAddress}
        value={address}
      />
      <TextInput
        style={styles.input}
        placeholder="Role (optional)"
        onChangeText={setRole}
        value={role}
      />
      <TextInput
        style={styles.input}
        placeholder="Password"
        secureTextEntry
        onChangeText={setPassword}
        value={password}
      />
      <TextInput
        style={styles.input}
        placeholder="Confirm Password"
        secureTextEntry
        onChangeText={setCPassword}
        value={c_password}
      />
      <TouchableOpacity style={styles.signUpButton} onPress={() => console.log("Registration Info", { firstname, lastname, birthdate, address, phone_number, email, role, password })}>
        <Text style={styles.signUpButtonText}>Sign Up</Text>
      </TouchableOpacity>
      <TouchableOpacity style={styles.signUpButton} onPress={() => navigation.goBack()}>
        <Text style={styles.signUpButtonText}>Go Back</Text>
      </TouchableOpacity>
    </View>
  );
};


const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
    backgroundColor: '#F5F5F5',
  },
  logo: {
    width: 80,
    height: 80,
    marginBottom: 20,
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 20,
  },
  input: {
    width: '80%',
    height: 40,
    borderWidth: 1,
    borderColor: '#ccc',
    borderRadius: 5,
    marginBottom: 10,
    paddingHorizontal: 10,
  },
  signUpButton: {
    backgroundColor: '#007AFF',
    paddingVertical: 10,
    paddingHorizontal: 20,
    borderRadius: 5,
  },
  signUpButtonText: {
    color: '#fff',
    fontSize: 18,
    fontWeight: 'bold',
  },
});


export default Registration;
