import React, { useEffect, useState } from 'react';
import { StyleSheet, View, Text, Image, TouchableOpacity, ScrollView, ImageSourcePropType } from 'react-native';
import AsyncStorage from '@react-native-async-storage/async-storage';
import { StackNavigationProp } from '@react-navigation/stack';
import { RouteProp } from '@react-navigation/native';
import { RootStackParamList } from './App';

type ProdInfoScreenNavigationProp = StackNavigationProp<RootStackParamList, 'ProdInfo'>;
type ProdInfoScreenRouteProp = RouteProp<RootStackParamList, 'ProdInfo'>;

type Props = {
  navigation: ProdInfoScreenNavigationProp;
  route: ProdInfoScreenRouteProp;
};

type Product = {
  name: string;
  price: number;
  description: string;
  image: ImageSourcePropType;
};

const ProdInfo: React.FC<Props> = ({ navigation, route }) => {
  const { item } = route.params;
  const [key, setKey] = useState(Date.now().toString());

  const addToCart = async (item: Product) => {
    try {
      // Get cart items if they exist
      const existingItems = await AsyncStorage.getItem('cartItems');
      let items: Product[] = [];
      if (existingItems !== null) {
        items = JSON.parse(existingItems);
      }
      // Add current product to the cart
      items.push(item);
      // Save updated products to AsyncStorage
      await AsyncStorage.setItem('cartItems', JSON.stringify(items));
      // Update key to force remount of the component
      setKey(Date.now().toString());
      // Navigate to the cart screen
      navigation.navigate('Cart');
    } catch (error) {
      console.error('Error adding to cart: ', error);
    }
  };

  return (
    <ScrollView key={key} contentContainerStyle={styles.container}>
      <View style={styles.header}>
        <Text style={styles.headerText}>{item.name}</Text>
        <Text style={styles.price}>${item.price}</Text>
      </View>

      <View style={styles.imageContainer}>
        <Image source={item.image} style={styles.image} />
      </View>

      <View style={styles.descriptionContainer}>
        <Text style={styles.description}>{item.description}</Text>
      </View>

      <View style={styles.buttonContainer}>
        <TouchableOpacity style={styles.button} onPress={() => addToCart(item)}>
          <Text style={styles.buttonText}>Add to Cart</Text>
        </TouchableOpacity>
      </View>
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: {
    flexGrow: 1,
    backgroundColor: '#f9f9f9',
    padding: 20,
  },
  header: {
    backgroundColor: '#ffffff',
    padding: 20,
    borderRadius: 10,
    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.25,
    shadowRadius: 3.84,
    elevation: 5,
    marginBottom: 20,
  },
  headerText: {
    fontSize: 22,
    fontWeight: 'bold',
    color: '#333',
  },
  price: {
    fontSize: 18,
    color: '#2a9d8f',
    marginTop: 8,
  },
  imageContainer: {
    marginBottom: 20,
    borderRadius: 10,
    overflow: 'hidden',
  },
  image: {
    width: '50%',
    height: 250,
    resizeMode: 'cover',
    alignSelf: 'center'
  },
  descriptionContainer: {
    backgroundColor: '#ffffff',
    padding: 20,
    borderRadius: 10,
    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.25,
    shadowRadius: 3.84,
    elevation: 5,
    marginBottom: 20,
  },
  description: {
    fontSize: 16,
    color: '#666',
  },
  buttonContainer: {
    alignItems: 'center',
  },
  button: {
    backgroundColor: '#e76f51',
    paddingVertical: 12,
    paddingHorizontal: 40,
    borderRadius: 20,
    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.25,
    shadowRadius: 3.84,
    elevation: 5,
  },
  buttonText: {
    color: '#ffffff',
    fontSize: 18,
    fontWeight: 'bold',
  },
});

export default ProdInfo;
