// MainScreen.tsx

import React, { useState, useEffect } from 'react';
import { View, Text, ScrollView, Image, ImageSourcePropType, StyleSheet, TextInput, TouchableOpacity } from 'react-native';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import { Ionicons } from '@expo/vector-icons';
import { useNavigation } from '@react-navigation/native';
import WishList from '../components/WishList';
import ShoppingCartView from '../components/Cart';

type JsonPlaceholder = {
  id: string;
  name: string;
  image: string;
  description: string;
  price: number;
}

const fetchData = async () => {
  const response = await fetch('http://localhost/geingeemu/public/api/videogame_index');
  return await response.json();
}

const HomeView = ({ data, addToCart }: { data: JsonPlaceholder[], addToCart: (item: JsonPlaceholder) => void }) => {
  const [searchQuery, setSearchQuery] = useState('');
  const navigation = useNavigation();

  const handleSearchPress = () => {
    navigation.navigate('Search');
  };

  const _buildImageWithText = (imagePath: ImageSourcePropType, text: string, textP: string, onPress: () => void) => (
    <TouchableOpacity onPress={onPress}>
      <View style={styles.imageContainer}>
        <Image source={imagePath} style={styles.image} />
        <View style={styles.textContainer}>
          <Text style={styles.productName} numberOfLines={2} ellipsizeMode="tail">{text}</Text>
          <Text style={styles.price}>{textP.length > 8 ? textP.substring(0, 8) : textP}</Text>
        </View>
      </View>
    </TouchableOpacity>
  );

  const filteredData = data.filter(item =>
    item.name.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <View style={styles.container}>
      <ScrollView>
        <TouchableOpacity onPress={handleSearchPress} style={styles.searchBarContainer}>
          <TextInput
            style={styles.searchInput}
            placeholder="Search"
            onChangeText={setSearchQuery}
            value={searchQuery}
            editable={false}
          />
        </TouchableOpacity>

        <Image source={require('../assets/images/banner.png')} style={{width: '100%', height: 125}} />

        <Image source={require('../assets/images/brands.png')} style={{width: '100%', height: 100, marginTop: 20}} />

        <Text style={styles.sectionHeader}>Featured</Text>
        <ScrollView horizontal={true}>
          <View style={{ flexDirection: 'row' }}>
            {filteredData.map(item => (
              <View style={{ paddingRight: 16 }} key={item.id}>
                {_buildImageWithText(item.image, item.name, `$${item.price}`, () => {
                  addToCart(item); // Add item to cart on press
                  navigation.navigate('ProdInfo', { item });
                })}
              </View>
            ))}
          </View>
        </ScrollView>

        <Text style={styles.sectionHeader}>Offers</Text>
        <ScrollView horizontal={true}>
          <View style={{ flexDirection: 'row' }}>
            {filteredData.map(item => (
              <View style={{ paddingRight: 16 }} key={item.id}>
                {_buildImageWithText(item.image, item.name, `$${item.price*0.9}`, () => {
                  addToCart(item); // Add item to cart on press
                  navigation.navigate('ProdInfo', { item });
                })}
              </View>
            ))}
          </View>
        </ScrollView>
      </ScrollView>
    </View>
  );
}

const MainScreen = () => {
  const [data, setData] = useState<JsonPlaceholder[]>([]);
  const [cartItems, setCartItems] = useState<JsonPlaceholder[]>([]); // State to store cart items

  useEffect(() => {
    fetchData().then(setData);
  }, []);

  const Tab = createBottomTabNavigator();

  // Function to add item to cart
  const addToCart = (item: JsonPlaceholder) => {
    setCartItems(prevItems => [...prevItems, item]);
  };

  // Function to remove item from cart
  const removeFromCart = (itemId: string) => {
    setCartItems(prevItems => prevItems.filter(item => item.id !== itemId));
  };

  return (
    <Tab.Navigator screenOptions={{ headerShown: false }}>
      <Tab.Screen
        name="Home"
        children={() => <HomeView data={data} addToCart={addToCart} />}
        options={{
          tabBarIcon: ({ color, size }) => (
            <Ionicons name="home" color={color} size={25} />
          ),
        }}
      />
      <Tab.Screen
        name="Cart"
        // Pass cartItems and removeFromCart as props to ShoppingCartView
        children={() => <ShoppingCartView cartItems={cartItems} removeFromCart={removeFromCart} />}
        options={{
          tabBarIcon: ({ color, size }) => (
            <Ionicons name="cart" color={color} size={25} />
          ),
        }}
      />
      <Tab.Screen
        name="WishList"
        component={WishList}
        options={{
          tabBarIcon: ({ color, size }) => (
            <Ionicons name="bookmark" color={color} size={25} />
          ),
        }}
      />
      <Tab.Screen
        name="Sell Games"
        component={HomeView}  // Assume this component similarly needs data
        options={{
          tabBarIcon: ({ color, size }) => (
            <Ionicons name="pricetag" color={color} size={25} />
          ),
        }}
      />
    </Tab.Navigator>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    margin: 16,
    backgroundColor: '#fff'
  },
  searchBarContainer: {
    borderWidth: 1,
    borderColor: '#ccc',
    borderRadius: 4,
    marginBottom: 16
  },
  searchInput: {
    borderWidth: 1,
    borderColor: '#ccc',
    borderRadius: 4,
    padding: 8,
    marginBottom: 16,
    height: 48
  },
  imageContainer: {
    marginRight: 16,
    width: 125,
  },
  image: {
    width: '100%',
    height: 190,
  },
  textContainer: {
    marginTop: 8,
    alignItems: 'baseline',
  },
  productName: {
    fontSize: 16,
    fontWeight: 'bold',
    overflow: 'hidden',
  },
  price: {
    fontSize: 16,
    fontWeight: 'bold',
    color: 'green',
    marginLeft: 8,
  },
  sectionHeader: {
    fontWeight: 'bold',
    fontSize: 30,
    marginBottom: 8,
  },
});

export default MainScreen;
